local Players = game:GetService("Players") local player = Players.LocalPlayer local playerGui = player:WaitForChild("PlayerGui") -- ScreenGui local gui = Instance.new("ScreenGui") gui.Name = "MoveTriggerJumpscareGui" gui.ResetOnSpawn = false gui.Parent = playerGui -- Jumpscare Image local image = Instance.new("ImageLabel") image.Size = UDim2.new(1, 0, 1, 0) image.BackgroundTransparency = 1 image.Image = "rbxassetid://9407437637" image.Visible = false image.Parent = gui -- Flash settings local flashes = 30 local flashTime = 0.4 -- Wait for character local character = player.Character or player.CharacterAdded:Wait() local humanoid = character:WaitForChild("Humanoid") local triggered = false -- Trigger when player moves humanoid.Running:Connect(function(speed) if speed > 0 and not triggered then triggered = true for i = 1, flashes do image.Visible = true task.wait(flashTime) image.Visible = false task.wait(flashTime) end end end)